select.execute:
var statement = [
        "select value, content from html",
        "where url=@url",
        "and xpath=@xpath",
        "and value matches @regex",
        '| sort(field="content")'
    ].join( " " ),

    regex = /\/regions\/([a-z]{3})\/.*$/i,

    query = y.query( statement, {
        url: "http://www.bctransit.com/",
        xpath: '//select[@name="jumplist"]/option',
        regex: regex.source
    }),

    xml = <regions/>;

for ( var i = 0, j = query.results.option.length(); i < j; i++ ) {
    var option = query.results.option[ i ],
        path = regex.exec( option.@value )[1],
        title = option.text();

    xml.regions += <region>
        <id>{i}</id>
        <path>{path}</path>
        <title>{title}</title>
    </region>;
}

response.object = xml;
